import React from 'react';
import { styled } from '@mui/material/styles';
import Container from '@mui/material/Container';
import Button from '../components/small-components/Button';
import TextBox from '../components/small-components/TextBox';
import * as Constantans from '../Constants';
import { UserRole } from './ChooseRolePage';



const RootContainer = styled(Container)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  paddingTop: theme.spacing(10),
  paddingBottom: theme.spacing(10),
}));

const FormContainer = styled('form')(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  width: '100%',
  marginTop: theme.spacing(2),
  marginBottom: theme.spacing(2),
}));

const ButtonWrapper = styled(Button)(({ theme }) => ({
  margin: theme.spacing(2),
}));

const ButtonSection = styled('div')(({theme}) => ({
    display: 'flex',
    flexDirection: 'row',
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '20px',

}));

const Title = styled('h1')(({ theme }) => ({
  marginBottom: theme.spacing(4),
}));

const QuestionContainer = styled('div')(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  width: '100%',
  marginBottom: theme.spacing(2),
}));

const Question = styled('h3')(({ theme }) => ({
  marginBottom: theme.spacing(1),
}));

function WrapQuestions(props){
  const {title, name, placeHolder} = props;
  return(
    <QuestionContainer>
            <Question>{name}</Question>
            <TextBox title={title} 
            placeholder={placeHolder} />
    </QuestionContainer>
  )
};

function questionType(UserRole){
  if(UserRole === 'mentor'){
    return(
      Constantans.MENTEE_QUESTIONS
    )
  }
  else{
    return(
      Constantans.MENTOR_QUESTIONS
    )
  }
}


const QuestionnairePage = () => {
  return (
    <>
    <RootContainer maxWidth="md">
        <FormContainer>
            <Title>Tell us about yourself</Title>
            {questionType(UserRole).map(x => 
            <WrapQuestions
            title= {x.title}
            name= {x.name}
            placeHolder={x.placeHolder}
            />
              )}
        <ButtonSection>
            <ButtonWrapper variant="contained" color="primary" href={Constantans.CHOOSE_ROLE_PAGE} title='Prev' />
            <ButtonWrapper variant="contained" color="primary" href={Constantans.HOME_PAGE}  title='Next' />
        </ButtonSection>

    </FormContainer>
</RootContainer>
</>
  );
};

export default QuestionnairePage;
