import React from 'react';



const UserRole = React.createContext();

export const RoleProvider = ({ children }) => {
    const [role, setRole] = React.useState({
       role: 'Blank',
    })
    // the value passed in here will be accessible anywhere in our application 
    // you can pass any value, in our case we pass our state and it's update method 
    return (
        <UserRole.Provider value={{role, setRole}}>
            {children}
        </UserRole.Provider>
    )
}

export const useRoleContext = React.useContext(UserRole);
